import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { MeetingsService } from '../../services/meetings.service';
import { NewMeetDetail } from 'src/app/shared/modals/newMeetDetail';
import { ActivatedRoute, Router } from '@angular/router';
import { UserDetail } from 'src/app/shared/modals/userDetail';

@Component({
  selector: 'app-meetings',
  templateUrl: './meetings.component.html',
  styleUrls: ['./meetings.component.scss']
})
export class MeetingsComponent implements OnInit {
  @ViewChild('videoElement', { static: false }) videoElement!: ElementRef;
  stream!: MediaStream;

  constructor(
    private _meetingServices: MeetingsService,
    private _activeRoute: ActivatedRoute,
    private _router: Router
  ) { }

  ngOnInit(): void {
    this._activeRoute.queryParams.subscribe(
      (res: any) => {
        if (res['meetId']) {
          this.loadMeetDetailsByMeetingCode(res['meetId']);
        }
      }
    )
  }

  meetingDetails: NewMeetDetail = new NewMeetDetail();

  loadMeetDetailsByMeetingCode(_meetId: string) {
    this.meetingDetails = this._meetingServices.getMeetDetailsByMeetCode(_meetId);
    if (this.meetingDetails.MeetCode) {
      this.loadUserList();
    }
  }

  userList: UserDetail[] = [];
  loadUserList() {
    let _localStoArray = localStorage;
    if (_localStoArray.length) {
      debugger
      for (let i = 0; i < localStorage.length; i++) {
        let _key = localStorage.key(i);
        if (_key?.toLowerCase().includes('user')) {
          let _userDetails: any = localStorage.getItem(_key);
          if (_userDetails) {
            _userDetails = JSON.parse(_userDetails);
            this.userList.push(_userDetails);
          }
        }
      }
    }
  }

  leaveMeeting() {
    // localStorage.removeItem('user');
    this._router.navigate(['dashboard']);
  }

  startCamera() {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        this.stream = stream;
        this.videoElement.nativeElement.srcObject = stream;
      })
      .catch((error) => {
        console.error('Error accessing camera:', error);
      });
  }

  stopCamera() {
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
    }
  }

  private mediaStream: MediaStream | null = null;

  async startScreenSharing() {
    try {
      this.mediaStream = await navigator.mediaDevices.getDisplayMedia({
        video: {
          displaySurface: 'browser',
        } as MediaTrackConstraints,
        audio: false
      });

      const videoElement = document.getElementById('screenVideo') as HTMLVideoElement;
      if (videoElement) {
        videoElement.srcObject = this.mediaStream;
      }

      this.mediaStream.getVideoTracks()[0].onended = () => {
        this.stopScreenSharing();
      };
    } catch (error) {
      console.error('Error capturing screen:', error);
    }
  }

  stopScreenSharing() {
    if (this.mediaStream) {
      this.mediaStream.getTracks().forEach(track => track.stop());
      this.mediaStream = null;
    }
  }


}
<ng-container *ngIf="true">
  <img src="assets/images/meeting/office_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('office')"
    style="height: 100dvh;" width="100%" alt srcset>
  <img src="assets/images/meeting/class_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('class')"
    style="height: 100dvh;" width="100%" alt srcset>
  <img src="assets/images/meeting/cabin_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('cabin')"
    style="height: 100dvh;" width="100%" alt srcset>
  <div class="card p-2 flex flex-column" style="position: absolute; right: 0;">
    <img src="assets/images/meeting_menubar_icons/mic_on.png" alt srcset>
    <img src="assets/images/meeting_menubar_icons/mic_off.png" alt srcset>
  </div>
  <div style="position: absolute; top: 2%; right: 2%;">
    <button type="button" class="btn btn-sm  text-white"
      style="background-color: red;" (click)="leaveMeeting()">
      <strong>Leave Meet</strong>
    </button>
  </div>

  <div class="flex justify-center w-100">
    <div class="flex flex-row gap-2"
      style="min-width: 80dvw; max-width: 80dvw; min-height: 90dvh; max-height: 90dvh; overflow-x: auto; position: absolute; bottom: 0;">

      <div class="flex flex-row gap-1 w-100">
        <div class="card p-2 flex flex-column gap-2"
          style="min-width: 25%; max-width: 25%; min-height: 85dvh; max-height: 85dvh; overflow-x: auto;">

          <strong>
            Candidate List
          </strong>
          <ng-container *ngFor="let user of userList">
            <div class="card p-3 flex flex-row mb-2">
              <img class="mask mask-heart"
                src="assets/images/meeting/user_image.png"
                style="height: 50px !important; width: 50px !important;" />
              <div class="flex flex-col">
                <strong>{{user.FirstName}}</strong>
                <strong><small>{{user.LastName}}</small></strong>
              </div>
            </div>
          </ng-container>
        </div>
        <div class="card p-2"
          style="min-width: 74% !important; max-width: 74% !important; min-height: 85dvh; max-height: 85dvh; overflow-x: auto;">
          <!-- <div class="flex flex-column gap-1">
            <div class="flex flex-row justify-content-between">
              <strong>
                Objective
              </strong>
              <input type="checkbox" checked="checked" role="switch"
                class="toggle border-indigo-600 bg-indigo-500 checked:bg-orange-400 checked:text-orange-800 checked:border-orange-500 " />
            </div>
          </div>
          <div class="camera-container">
            <video #videoElement autoplay></video>
            <div class="flex flex-row justify-content-between mt-2">
              <button class="btn btn-primary" (click)="startCamera()">Start Camera</button>
              <button class="btn btn-primary" (click)="stopCamera()">Stop Camera</button>
            </div>
          </div> -->
          <div>
            <button (click)="startScreenSharing()">Start Screen Sharing</button>
            <button (click)="stopScreenSharing()">Stop Screen Sharing</button>
            <video id="screenVideo" autoplay playsinline
              style="width: 100%; border: 2px solid black;"></video>
          </div>
        </div>
      </div>
    </div>
  </div>
</ng-container>
